tickers = sorted(tickers)

class Tracker:
    def __init__(self, filename = 'tracker', intraday=None, daily=True, interval=1):
        self.filename = './config/' + filename + '.xlsx'
        self.intraday = intraday
        self.daily = daily
        self.interval = interval
        self.load_dataframe()
        
    def load_dataframe(self):
        if not os.path.exists('config'):
            os.mkdir('config')
        if not os.path.exists(self.filename):
            wb = openpyxl.Workbook()
            ws = wb.active
            yesterday = datetime.datetime.now() - datetime.timedelta(1)
            ws.append(['ticker', 'date', 'retries'])
            for t in tickers:
                ws.append([t, yesterday, 0])
            wb.save(self.filename)
            wb.close()
        wb = openpyxl.load_workbook(self.filename)
        ws = wb.active
        data = ws.values
        wb.close()
        cols = next(data)[1:]
        cols = [c.lower() for c in cols]
        data = list(data)
        idx = [r[0] for r in data]
        data = (islice(r, 1, None) for r in data)
        self.df = pd.DataFrame(data, index=idx, columns=cols).reset_index()
        self.df.columns = ['ticker', 'date', 'retries']
        self.df.ticker = self.df.ticker.astype(str)
        self.df.date = pd.to_datetime(self.df.date)
        self.df.retries = pd.to_numeric(self.df.retries)
        
    def save_dataframe(self):
        if not os.path.exists('config'):
            os.mkdir('config')
        wb = openpyxl.Workbook()
        ws = wb.active
        for r in dataframe_to_rows(self.df, index=False, header=True):
            ws.append(r)
        wb.save(self.filename)
        wb.save('./config/tracker_backup.xlsx')
        wb.close()
        
    def run_tracker(self):
        self.df.sort_values(by=['retries', 'date', 'ticker'], ascending=[False, True, True], inplace=True)
        for i, row in self.df.iterrows():
            print(row.ticker)
            if (datetime.datetime.now() - row.date).days > 0:
                try:
                    Stock(row.ticker, api_key, self.intraday, self.daily)
                    self.df.at[i, 'date'] = datetime.datetime.now()
                    self.df.at[i, 'retries'] = 0
                except:
                    self.df.at[i, 'retries'] += 1
            else:
                self.df.at[i, 'retries'] = 0
            self.save_dataframe()

if __name__ == "__main__":
    x = Tracker()
    x.run_tracker()

